#include "../common.h"
//https://leetcode.com/problems/permutation-in-string
bool checkMapsEquality(map<char, int> m1, map<char, int> m2) {
	map<char, int>::iterator it1 = m1.begin();
	map<char, int>::iterator it2 = m2.begin();

	if (m1.size() != m2.size())
		return false;

	while (it1 != m1.end() && it2 != m2.end()) {

		if (it1->first != it2->first || it1->second != it2->second) {
			return false;
		}
		it1++;
		it2++;
	}
	return true;
}

bool checkInclusion(string s1, string s2) {
	if (s1.length() > s2.length())
		return false;

	map<char, int> mapper;
	map<char, int> checker;

	for (char c = 'a'; c <= 'z'; c++) {
		mapper[c] = 0;
		checker[c] = 0;
	}

	for (int i = 0; i < s1.length(); i++)
		mapper[s1[i]]++;

	int iStart = 0;
	int windowSize = s1.size();
	int iEnd = windowSize;
	int strSize = s2.length();

	//load the first shot
	while (iStart < iEnd) {
		checker[s2[iStart]]++;
		iStart++;
	}

	if (checkMapsEquality(mapper, checker))
		return true;

	iStart = iEnd - windowSize;

	while (iEnd < strSize) {
		checker[s2[iStart]]--;
		checker[s2[iEnd]]++;
		if (checkMapsEquality(mapper, checker))
			return true;
		iStart++;
		iEnd++;

	}
	return false;
}

int main()
{
	/*
	string s1 = ""
		, s2 = "";
	*/
	//string s1 = "adc", s2 = "dcda";

	//string s1 = "ab", s2 = "eidboaoo";
	string s1 = "dinitrophenylhydrazine", s2 = "dimethylhydrazine";
	cout << checkInclusion(s1, s2);

	return 0;
}
